mapper.select:
select mnum, mname, mid, email, phone, addr, 
regdate, grade, mstatus, birth, FLOOR((months_between(sysdate, birth))/12) age, sex 
from members where mstatus = 1 and mnum = #{mnum}

select mnum, mname, mid, email, phone, addr, 
regdate, grade, mstatus, birth, FLOOR((months_between(sysdate, birth))/12) age, sex 
from members where mstatus = 0 and mnum = #{mnum}
